var colors = require('colors');
var path = require('path');

exports.error = function(input) {
	console.log('ERROR:'.bold.red, input);
};

exports.success = function(input) {
	console.log(input.green.bold);
};

exports.formatted = function(input) {
	console.log('');
	console.log('|', input.number.bold + ' ' +  input.title.bold);
	console.log('');
	console.log('Type:'.bold, input.type);
	console.log('');
	console.log(input.description);
};

exports.help = function() {
	console.log('');
	console.log('Usage:', path.basename(process.argv[1]), '<http-code>');
	process.exit();
};

exports.invalid = function() {
	console.log('');
	exports.error('Invalid code');
	exports.help();
};